using NiDUC_RS.GaloisField;
using NiDUC_RS.RS_Coder;

var reedSalomonCoder = new ReedSolomonCoder(6, 4);
const string packet = "";
const string error =  "";
//                     _     _     _     _     _     
var received = reedSalomonCoder.DecodeMessage(error);
var compare = string.Compare(received, packet, StringComparison.Ordinal);

Console.WriteLine($"Successful decoding result: {compare == 0}");